    box-shadow: 3px 0px 10px 5px #cc2525;
}
.button:hover {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
.textAreaContainer:focus-within{
	-webkit-box-shadow: inset 0 0px 1px rgba(0,0,0,.075), 0 0 1px rgba(205, 35, 35, 0.6);
	box-shadow: inset 0 0px 1px rgba(0,0,0,.075), 0 0 1px rgba(205, 35, 35, 0.6);
	border: solid 1px #cc2525;
}
.slider-selection {
    background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
    background-image: -o-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
    background-image: linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#8a1515',endColorstr='#850a0a',GradientType=0);
}
.slider-handle {
	background-color: #770205;
    background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: -o-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: linear-gradient(to bottom,#8a1515 0,#a71500 100%);
}
.slider-tick.in-selection {
    background-image: -webkit-linear-gradient(to bottom,#aa1010,#8a1515 100%);
    background-image: -o-linear-gradient(to bottom,#aa1010,#8a1515 100%);
	background-image: linear-gradient(to bottom,#aa1010,#8a1515 100%);
}
.btn-primary {
    background-color: #8a1515;
    border-color: #3c153c;
}

.btn-primary:hover {
	-webkit-box-shadow: inset 0 0 20px 9px #8a1515;
	box-shadow: inset 0 0 20px 9px #8a1515;
    background-color: rgb(140, 10, 140);
}

.btn-primary:focus {
	background-color: #8a1515;
    border-color: #3c153c;
}
.progress-bar {
	background-color: #8a1515;
}
.switchContainer.active {
	background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
	background-image: -o-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
	background-image: linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
	filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#8a1515',endColorstr='#850a0a',GradientType=0);
}
.switchContainer > .slider-tick-container > .switchOn  {
	background-color: #770205;
    background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: -o-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: linear-gradient(to bottom,#8a1515 0,#a71500 100%);
}
